import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { useForm } from 'react-hook-form';
import { 
  GitBranch, 
  ArrowRight, 
  Plus, 
  Trash2, 
  Eye, 
  Search,
  AlertCircle,
  CheckCircle,
  XCircle
} from 'lucide-react';
import toast from 'react-hot-toast';
import apiService from '../services/api';
import { MAPPING_EQUIVALENCE, TRADITIONAL_SYSTEMS, CODE_TYPES, TOAST_MESSAGES } from '../constants';

const MappingPage = () => {
  const [selectedCode, setSelectedCode] = useState('');
  const [selectedSystem, setSelectedSystem] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const queryClient = useQueryClient();

  const { data: mappings, isLoading: mappingsLoading } = useQuery(
    ['mappings', selectedCode, selectedSystem],
    () => apiService.getMappingsForCode(selectedSystem, selectedCode),
    {
      enabled: !!(selectedCode && selectedSystem),
    }
  );

  const { data: namasteCodes } = useQuery(
    'namaste-codes',
    () => apiService.getNamasteBySystem(TRADITIONAL_SYSTEMS.AYURVEDA),
    { enabled: false }
  );

  const { data: icd11Codes } = useQuery(
    'icd11-codes',
    () => apiService.getIcd11ByType(CODE_TYPES.TM2),
    { enabled: false }
  );

  const createMappingMutation = useMutation(apiService.createMapping, {
    onSuccess: () => {
      toast.success(TOAST_MESSAGES.SUCCESS.MAPPING_CREATED);
      queryClient.invalidateQueries(['mappings']);
      setShowCreateForm(false);
    },
    onError: (error) => {
      toast.error(error.message || TOAST_MESSAGES.ERROR.GENERIC);
    },
  });

  const deleteMappingMutation = useMutation(apiService.deleteMapping, {
    onSuccess: () => {
      toast.success('Mapping deleted successfully');
      queryClient.invalidateQueries(['mappings']);
    },
    onError: (error) => {
      toast.error(error.message || TOAST_MESSAGES.ERROR.GENERIC);
    },
  });

  const { register, handleSubmit, reset, formState: { errors } } = useForm();

  const onSubmit = (data) => {
    createMappingMutation.mutate(data);
  };

  const handleDeleteMapping = (id) => {
    if (window.confirm('Are you sure you want to delete this mapping?')) {
      deleteMappingMutation.mutate(id);
    }
  };

  const getEquivalenceIcon = (equivalence) => {
    switch (equivalence) {
      case MAPPING_EQUIVALENCE.EQUIVALENT:
      case MAPPING_EQUIVALENCE.EQUAL:
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case MAPPING_EQUIVALENCE.RELATEDTO:
      case MAPPING_EQUIVALENCE.INEXACT:
        return <AlertCircle className="h-4 w-4 text-yellow-500" />;
      case MAPPING_EQUIVALENCE.UNMATCHED:
      case MAPPING_EQUIVALENCE.DISJOINT:
        return <XCircle className="h-4 w-4 text-red-500" />;
      default:
        return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getEquivalenceColor = (equivalence) => {
    switch (equivalence) {
      case MAPPING_EQUIVALENCE.EQUIVALENT:
      case MAPPING_EQUIVALENCE.EQUAL:
        return 'bg-green-100 text-green-800';
      case MAPPING_EQUIVALENCE.RELATEDTO:
      case MAPPING_EQUIVALENCE.INEXACT:
        return 'bg-yellow-100 text-yellow-800';
      case MAPPING_EQUIVALENCE.UNMATCHED:
      case MAPPING_EQUIVALENCE.DISJOINT:
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const MappingCard = ({ mapping }) => (
    <div className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-center space-x-3 mb-3">
            <div className="flex items-center space-x-2">
              <span className="text-sm font-medium text-gray-900">{mapping.sourceCode}</span>
              <span className="text-xs text-gray-500">({mapping.sourceSystem})</span>
            </div>
            <ArrowRight className="h-4 w-4 text-gray-400" />
            <div className="flex items-center space-x-2">
              <span className="text-sm font-medium text-gray-900">{mapping.targetCode}</span>
              <span className="text-xs text-gray-500">({mapping.targetSystem})</span>
            </div>
          </div>
          
          <div className="flex items-center space-x-2 mb-2">
            {getEquivalenceIcon(mapping.equivalence)}
            <span className={`badge ${getEquivalenceColor(mapping.equivalence)}`}>
              {mapping.equivalence}
            </span>
            {mapping.confidenceScore && (
              <span className="text-xs text-gray-500">
                Confidence: {(mapping.confidenceScore * 100).toFixed(1)}%
              </span>
            )}
          </div>

          {mapping.comment && (
            <p className="text-sm text-gray-600 mb-2">{mapping.comment}</p>
          )}

          <div className="flex items-center space-x-4 text-xs text-gray-500">
            <span>Created: {new Date(mapping.createdAt).toLocaleDateString()}</span>
            {mapping.mappingVersion && <span>Version: {mapping.mappingVersion}</span>}
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          <button className="p-2 text-gray-400 hover:text-gray-600">
            <Eye className="h-4 w-4" />
          </button>
          <button 
            onClick={() => handleDeleteMapping(mapping.id)}
            className="p-2 text-gray-400 hover:text-red-600"
          >
            <Trash2 className="h-4 w-4" />
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Concept Mapping</h1>
          <p className="mt-1 text-sm text-gray-500">
            Manage mappings between NAMASTE and ICD-11 codes
          </p>
        </div>
        <button
          onClick={() => setShowCreateForm(true)}
          className="btn-primary"
        >
          <Plus className="h-4 w-4 mr-2" />
          Create Mapping
        </button>
      </div>

      {/* Search Section */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Search Mappings</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label htmlFor="system" className="block text-sm font-medium text-gray-700 mb-2">
                Source System
              </label>
              <select
                id="system"
                value={selectedSystem}
                onChange={(e) => setSelectedSystem(e.target.value)}
                className="input"
              >
                <option value="">Select System</option>
                <option value="NAMASTE">NAMASTE</option>
                <option value="ICD11-TM2">ICD-11 TM2</option>
                <option value="ICD11-BIOMEDICINE">ICD-11 Biomedicine</option>
              </select>
            </div>
            <div>
              <label htmlFor="code" className="block text-sm font-medium text-gray-700 mb-2">
                Code
              </label>
              <input
                type="text"
                id="code"
                value={selectedCode}
                onChange={(e) => setSelectedCode(e.target.value)}
                className="input"
                placeholder="Enter code..."
              />
            </div>
            <div className="flex items-end">
              <button
                onClick={() => {
                  if (selectedCode && selectedSystem) {
                    // Trigger query
                  }
                }}
                className="btn-primary w-full"
              >
                <Search className="h-4 w-4 mr-2" />
                Search Mappings
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Results */}
      {selectedCode && selectedSystem && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                Mappings for {selectedCode} ({selectedSystem})
              </h3>
              {mappings && mappings.length > 0 && (
                <span className="text-sm text-gray-500">
                  {mappings.length} mapping(s) found
                </span>
              )}
            </div>

            {mappingsLoading ? (
              <div className="flex items-center justify-center py-12">
                <div className="loading-spinner"></div>
                <span className="ml-3 text-gray-600">Loading mappings...</span>
              </div>
            ) : mappings && mappings.length > 0 ? (
              <div className="space-y-4">
                {mappings.map((mapping) => (
                  <MappingCard key={mapping.id} mapping={mapping} />
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <GitBranch className="mx-auto h-12 w-12 text-gray-400" />
                <h3 className="mt-2 text-sm font-medium text-gray-900">No mappings found</h3>
                <p className="mt-1 text-sm text-gray-500">
                  No mappings exist for this code. Create a new mapping to get started.
                </p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Create Mapping Modal */}
      {showCreateForm && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={() => setShowCreateForm(false)} />
            
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <form onSubmit={handleSubmit(onSubmit)}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <div className="sm:flex sm:items-start">
                    <div className="mt-3 text-center sm:mt-0 sm:text-left w-full">
                      <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                        Create New Mapping
                      </h3>
                      
                      <div className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Source Code
                            </label>
                            <input
                              {...register('sourceCode', { required: 'Source code is required' })}
                              className="input"
                              placeholder="e.g., NAM001"
                            />
                            {errors.sourceCode && (
                              <p className="mt-1 text-sm text-red-600">{errors.sourceCode.message}</p>
                            )}
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Source System
                            </label>
                            <select
                              {...register('sourceSystem', { required: 'Source system is required' })}
                              className="input"
                            >
                              <option value="">Select System</option>
                              <option value="NAMASTE">NAMASTE</option>
                              <option value="ICD11-TM2">ICD-11 TM2</option>
                              <option value="ICD11-BIOMEDICINE">ICD-11 Biomedicine</option>
                            </select>
                            {errors.sourceSystem && (
                              <p className="mt-1 text-sm text-red-600">{errors.sourceSystem.message}</p>
                            )}
                          </div>
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Target Code
                            </label>
                            <input
                              {...register('targetCode', { required: 'Target code is required' })}
                              className="input"
                              placeholder="e.g., TM2-001"
                            />
                            {errors.targetCode && (
                              <p className="mt-1 text-sm text-red-600">{errors.targetCode.message}</p>
                            )}
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Target System
                            </label>
                            <select
                              {...register('targetSystem', { required: 'Target system is required' })}
                              className="input"
                            >
                              <option value="">Select System</option>
                              <option value="NAMASTE">NAMASTE</option>
                              <option value="ICD11-TM2">ICD-11 TM2</option>
                              <option value="ICD11-BIOMEDICINE">ICD-11 Biomedicine</option>
                            </select>
                            {errors.targetSystem && (
                              <p className="mt-1 text-sm text-red-600">{errors.targetSystem.message}</p>
                            )}
                          </div>
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">
                            Equivalence
                          </label>
                          <select
                            {...register('equivalence', { required: 'Equivalence is required' })}
                            className="input"
                          >
                            <option value="">Select Equivalence</option>
                            {Object.values(MAPPING_EQUIVALENCE).map((equivalence) => (
                              <option key={equivalence} value={equivalence}>
                                {equivalence}
                              </option>
                            ))}
                          </select>
                          {errors.equivalence && (
                            <p className="mt-1 text-sm text-red-600">{errors.equivalence.message}</p>
                          )}
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">
                            Comment (Optional)
                          </label>
                          <textarea
                            {...register('comment')}
                            rows={3}
                            className="input"
                            placeholder="Add a comment about this mapping..."
                          />
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">
                            Confidence Score (Optional)
                          </label>
                          <input
                            {...register('confidenceScore', { 
                              min: { value: 0, message: 'Score must be between 0 and 1' },
                              max: { value: 1, message: 'Score must be between 0 and 1' }
                            })}
                            type="number"
                            step="0.1"
                            min="0"
                            max="1"
                            className="input"
                            placeholder="0.0 - 1.0"
                          />
                          {errors.confidenceScore && (
                            <p className="mt-1 text-sm text-red-600">{errors.confidenceScore.message}</p>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <button
                    type="submit"
                    disabled={createMappingMutation.isLoading}
                    className="btn-primary sm:ml-3 sm:w-auto"
                  >
                    {createMappingMutation.isLoading ? 'Creating...' : 'Create Mapping'}
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowCreateForm(false);
                      reset();
                    }}
                    className="btn-secondary mt-3 sm:mt-0 sm:w-auto"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export { MappingPage };
