import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { 
  Settings, 
  Database, 
  RefreshCw, 
  Download, 
  Upload,
  Activity,
  AlertTriangle,
  CheckCircle,
  Clock,
  BarChart3
} from 'lucide-react';
import toast from 'react-hot-toast';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import apiService from '../services/api';
import { TOAST_MESSAGES } from '../constants';

const AdminPage = () => {
  const [activeSection, setActiveSection] = useState('overview');
  const queryClient = useQueryClient();

  const { data: stats, isLoading: statsLoading } = useQuery('stats', apiService.getStats);

  const generateMappingsMutation = useMutation(apiService.generateMappings, {
    onSuccess: () => {
      toast.success('Automatic mapping generation initiated');
      queryClient.invalidateQueries('stats');
    },
    onError: (error) => {
      toast.error(error.message || TOAST_MESSAGES.ERROR.GENERIC);
    },
  });

  const reloadNamasteMutation = useMutation(apiService.reloadNamasteData, {
    onSuccess: () => {
      toast.success(TOAST_MESSAGES.SUCCESS.DATA_RELOADED);
      queryClient.invalidateQueries('stats');
    },
    onError: (error) => {
      toast.error(error.message || TOAST_MESSAGES.ERROR.GENERIC);
    },
  });

  const syncIcd11Mutation = useMutation(apiService.syncIcd11Data, {
    onSuccess: () => {
      toast.success(TOAST_MESSAGES.SUCCESS.SYNC_COMPLETED);
      queryClient.invalidateQueries('stats');
    },
    onError: (error) => {
      toast.error(error.message || TOAST_MESSAGES.ERROR.GENERIC);
    },
  });

  const adminActions = [
    {
      id: 'generate-mappings',
      title: 'Generate Automatic Mappings',
      description: 'Generate mappings based on existing WHO terminology codes',
      icon: RefreshCw,
      action: () => generateMappingsMutation.mutate(),
      loading: generateMappingsMutation.isLoading,
      color: 'bg-blue-500',
    },
    {
      id: 'reload-namaste',
      title: 'Reload NAMASTE Data',
      description: 'Reload NAMASTE codes from CSV file',
      icon: Database,
      action: () => reloadNamasteMutation.mutate(),
      loading: reloadNamasteMutation.isLoading,
      color: 'bg-green-500',
    },
    {
      id: 'sync-icd11',
      title: 'Sync ICD-11 Data',
      description: 'Synchronize ICD-11 data from WHO API',
      icon: Download,
      action: () => syncIcd11Mutation.mutate(),
      loading: syncIcd11Mutation.isLoading,
      color: 'bg-purple-500',
    },
  ];

  const systemHealth = [
    {
      name: 'API Server',
      status: 'healthy',
      uptime: '99.9%',
      lastCheck: '2 minutes ago',
    },
    {
      name: 'Database',
      status: 'healthy',
      uptime: '99.8%',
      lastCheck: '1 minute ago',
    },
    {
      name: 'ICD-11 API',
      status: 'healthy',
      uptime: '98.5%',
      lastCheck: '5 minutes ago',
    },
    {
      name: 'FHIR Server',
      status: 'healthy',
      uptime: '99.7%',
      lastCheck: '3 minutes ago',
    },
  ];

  const recentActivities = [
    {
      id: 1,
      action: 'Data Sync',
      description: 'ICD-11 data synchronized successfully',
      timestamp: '2 minutes ago',
      status: 'success',
    },
    {
      id: 2,
      action: 'Mapping Created',
      description: 'New mapping created: NAM001 â†’ TM2-001',
      timestamp: '15 minutes ago',
      status: 'success',
    },
    {
      id: 3,
      action: 'Bundle Upload',
      description: 'FHIR bundle processed with 25 entries',
      timestamp: '1 hour ago',
      status: 'success',
    },
    {
      id: 4,
      action: 'System Backup',
      description: 'Daily backup completed successfully',
      timestamp: '2 hours ago',
      status: 'success',
    },
  ];

  const getStatusIcon = (status) => {
    switch (status) {
      case 'healthy':
      case 'success':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4 text-yellow-500" />;
      case 'error':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'healthy':
      case 'success':
        return 'text-green-600 bg-green-100';
      case 'warning':
        return 'text-yellow-600 bg-yellow-100';
      case 'error':
        return 'text-red-600 bg-red-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const sections = [
    { id: 'overview', name: 'Overview', icon: BarChart3 },
    { id: 'actions', name: 'Admin Actions', icon: Settings },
    { id: 'health', name: 'System Health', icon: Activity },
    { id: 'activity', name: 'Recent Activity', icon: Clock },
  ];

  const chartData = stats ? [
    { name: 'Ayurveda', value: stats.ayurvedaCount },
    { name: 'Siddha', value: stats.siddhaCount },
    { name: 'Unani', value: stats.unaniCount },
  ] : [];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Administration</h1>
        <p className="mt-1 text-sm text-gray-500">
          System administration and monitoring tools
        </p>
      </div>

      {/* Navigation */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <div className="flex flex-wrap gap-2">
            {sections.map((section) => {
              const Icon = section.icon;
              return (
                <button
                  key={section.id}
                  onClick={() => setActiveSection(section.id)}
                  className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                    activeSection === section.id
                      ? 'bg-primary-100 text-primary-700'
                      : 'text-gray-500 hover:text-gray-700 hover:bg-gray-50'
                  }`}
                >
                  <Icon className="mr-2 h-4 w-4" />
                  {section.name}
                </button>
              );
            })}
          </div>
        </div>
      </div>

      {/* Overview Section */}
      {activeSection === 'overview' && (
        <div className="space-y-6">
          {/* Stats Grid */}
          <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <Database className="h-6 w-6 text-blue-600" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">NAMASTE Codes</dt>
                      <dd className="text-lg font-medium text-gray-900">
                        {statsLoading ? (
                          <div className="animate-pulse bg-gray-200 h-6 w-16 rounded"></div>
                        ) : (
                          stats?.namasteCodeCount?.toLocaleString() || '0'
                        )}
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <Database className="h-6 w-6 text-green-600" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">ICD-11 Codes</dt>
                      <dd className="text-lg font-medium text-gray-900">
                        {statsLoading ? (
                          <div className="animate-pulse bg-gray-200 h-6 w-16 rounded"></div>
                        ) : (
                          stats?.icd11CodeCount?.toLocaleString() || '0'
                        )}
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <RefreshCw className="h-6 w-6 text-purple-600" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">Mappings</dt>
                      <dd className="text-lg font-medium text-gray-900">
                        {statsLoading ? (
                          <div className="animate-pulse bg-gray-200 h-6 w-16 rounded"></div>
                        ) : (
                          stats?.mappingCount?.toLocaleString() || '0'
                        )}
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <Activity className="h-6 w-6 text-red-600" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">System Status</dt>
                      <dd className="text-lg font-medium text-green-600">Operational</dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Chart */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Traditional Medicine Systems Distribution</h3>
            {statsLoading ? (
              <div className="flex items-center justify-center h-64">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
              </div>
            ) : (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#3b82f6" />
                </BarChart>
              </ResponsiveContainer>
            )}
          </div>
        </div>
      )}

      {/* Admin Actions Section */}
      {activeSection === 'actions' && (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Administrative Actions</h3>
              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
                {adminActions.map((action) => {
                  const Icon = action.icon;
                  return (
                    <div key={action.id} className="relative group bg-white p-6 focus-within:ring-2 focus-within:ring-inset focus-within:ring-primary-500 rounded-lg border border-gray-200 hover:border-gray-300 transition-colors">
                      <div>
                        <span className={`rounded-lg inline-flex p-3 ${action.color} text-white`}>
                          <Icon className="h-6 w-6" />
                        </span>
                      </div>
                      <div className="mt-4">
                        <h3 className="text-lg font-medium text-gray-900">
                          {action.title}
                        </h3>
                        <p className="mt-2 text-sm text-gray-500">
                          {action.description}
                        </p>
                      </div>
                      <div className="mt-4">
                        <button
                          onClick={action.action}
                          disabled={action.loading}
                          className="btn-primary w-full"
                        >
                          {action.loading ? (
                            <>
                              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                              Processing...
                            </>
                          ) : (
                            'Execute'
                          )}
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* System Health Section */}
      {activeSection === 'health' && (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">System Health Status</h3>
              <div className="space-y-4">
                {systemHealth.map((service, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center">
                      {getStatusIcon(service.status)}
                      <div className="ml-3">
                        <p className="text-sm font-medium text-gray-900">{service.name}</p>
                        <p className="text-sm text-gray-500">Uptime: {service.uptime}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-4">
                      <span className={`badge ${getStatusColor(service.status)}`}>
                        {service.status}
                      </span>
                      <span className="text-xs text-gray-500">{service.lastCheck}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Recent Activity Section */}
      {activeSection === 'activity' && (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Recent Activity</h3>
              <div className="space-y-4">
                {recentActivities.map((activity) => (
                  <div key={activity.id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center">
                      {getStatusIcon(activity.status)}
                      <div className="ml-3">
                        <p className="text-sm font-medium text-gray-900">{activity.action}</p>
                        <p className="text-sm text-gray-500">{activity.description}</p>
                      </div>
                    </div>
                    <span className="text-xs text-gray-500">{activity.timestamp}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export { AdminPage };
