import React from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { 
  Search, 
  GitBranch, 
  FileText, 
  Database,
  TrendingUp,
  Activity,
  Shield,
  Users
} from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import apiService from '../services/api';
import { TRADITIONAL_SYSTEMS, SYSTEM_COLORS, CHART_CONFIG } from '../constants';

const Dashboard = () => {
  const { data: stats, isLoading: statsLoading } = useQuery('stats', apiService.getStats);

  const systemData = stats ? [
    { name: 'Ayurveda', value: stats.ayurvedaCount, color: SYSTEM_COLORS.AYURVEDA.primary },
    { name: 'Siddha', value: stats.siddhaCount, color: SYSTEM_COLORS.SIDDHA.primary },
    { name: 'Unani', value: stats.unaniCount, color: SYSTEM_COLORS.UNANI.primary },
  ] : [];

  const codeTypeData = stats ? [
    { name: 'TM2', value: stats.tm2Count, color: SYSTEM_COLORS.TM2.primary },
    { name: 'Biomedicine', value: stats.biomedicineCount, color: SYSTEM_COLORS.BIOMEDICINE.primary },
  ] : [];

  const quickActions = [
    {
      name: 'Search Codes',
      description: 'Search NAMASTE and ICD-11 codes',
      href: '/search',
      icon: Search,
      color: 'bg-blue-500',
    },
    {
      name: 'View Mappings',
      description: 'Browse concept mappings',
      href: '/mapping',
      icon: GitBranch,
      color: 'bg-green-500',
    },
    {
      name: 'FHIR Tools',
      description: 'FHIR-compliant operations',
      href: '/fhir',
      icon: FileText,
      color: 'bg-purple-500',
    },
    {
      name: 'Problem List',
      description: 'Dual-coded conditions',
      href: '/problem-list',
      icon: Database,
      color: 'bg-orange-500',
    },
    {
      name: 'Admin Panel',
      description: 'System administration',
      href: '/admin',
      icon: Shield,
      color: 'bg-red-500',
    },
  ];

  const StatCard = ({ title, value, icon: Icon, color, loading }) => (
    <div className="bg-white overflow-hidden shadow rounded-lg">
      <div className="p-5">
        <div className="flex items-center">
          <div className="flex-shrink-0">
            <Icon className={`h-6 w-6 ${color}`} />
          </div>
          <div className="ml-5 w-0 flex-1">
            <dl>
              <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
              <dd className="text-lg font-medium text-gray-900">
                {loading ? (
                  <div className="animate-pulse bg-gray-200 h-6 w-16 rounded"></div>
                ) : (
                  value?.toLocaleString() || '0'
                )}
              </dd>
            </dl>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="mt-1 text-sm text-gray-500">
          NAMASTE-ICD11 FHIR Terminology Service Overview
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <StatCard
          title="NAMASTE Codes"
          value={stats?.namasteCodeCount}
          icon={Database}
          color="text-blue-600"
          loading={statsLoading}
        />
        <StatCard
          title="ICD-11 Codes"
          value={stats?.icd11CodeCount}
          icon={Database}
          color="text-green-600"
          loading={statsLoading}
        />
        <StatCard
          title="Mappings"
          value={stats?.mappingCount}
          icon={GitBranch}
          color="text-purple-600"
          loading={statsLoading}
        />
        <StatCard
          title="Total Systems"
          value="3"
          icon={Shield}
          color="text-red-600"
          loading={false}
        />
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Traditional Systems Distribution */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Traditional Medicine Systems</h3>
          {statsLoading ? (
            <div className="flex items-center justify-center h-64">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
            </div>
          ) : (
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={systemData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {systemData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>

        {/* ICD-11 Code Types */}
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900 mb-4">ICD-11 Code Types</h3>
          {statsLoading ? (
            <div className="flex items-center justify-center h-64">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
            </div>
          ) : (
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={codeTypeData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="value" fill="#3b82f6" />
              </BarChart>
            </ResponsiveContainer>
          )}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">Quick Actions</h3>
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            {quickActions.map((action) => {
              const Icon = action.icon;
              return (
                <Link
                  key={action.name}
                  to={action.href}
                  className="relative group bg-white p-6 focus-within:ring-2 focus-within:ring-inset focus-within:ring-primary-500 rounded-lg border border-gray-200 hover:border-gray-300 transition-colors"
                >
                  <div>
                    <span className={`rounded-lg inline-flex p-3 ${action.color} text-white`}>
                      <Icon className="h-6 w-6" />
                    </span>
                  </div>
                  <div className="mt-4">
                    <h3 className="text-lg font-medium text-gray-900">
                      {action.name}
                    </h3>
                    <p className="mt-2 text-sm text-gray-500">
                      {action.description}
                    </p>
                  </div>
                  <span
                    className="pointer-events-none absolute top-6 right-6 text-gray-300 group-hover:text-gray-400"
                    aria-hidden="true"
                  >
                    <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M20 4h1a1 1 0 00-1-1v1zm-1 12a1 1 0 102 0h-2zM8 3a1 1 0 000 2V3zM3.293 19.293a1 1 0 101.414 1.414l-1.414-1.414zM19 4v12h2V4h-2zm1-1H8v2h12V3zm-.707.293l-16 16 1.414 1.414 16-16-1.414-1.414z" />
                    </svg>
                  </span>
                </Link>
              );
            })}
          </div>
        </div>
      </div>

      {/* System Information */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">System Information</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <Activity className="h-5 w-5 text-green-500 mr-3" />
              <div>
                <p className="text-sm font-medium text-gray-900">API Status</p>
                <p className="text-sm text-green-600">Operational</p>
              </div>
            </div>
            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <Database className="h-5 w-5 text-blue-500 mr-3" />
              <div>
                <p className="text-sm font-medium text-gray-900">Database</p>
                <p className="text-sm text-blue-600">Connected</p>
              </div>
            </div>
            <div className="flex items-center p-4 bg-gray-50 rounded-lg">
              <Shield className="h-5 w-5 text-purple-500 mr-3" />
              <div>
                <p className="text-sm font-medium text-gray-900">Security</p>
                <p className="text-sm text-purple-600">ABHA Enabled</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export { Dashboard };
